
public class IncreDecre {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int r=6;
		System.out.println("r=:"+ r++);
		System.out.println("r=:"+r);
		
		int x=6;
		
		System.out.println("x=:"+ x--);
		System.out.println("x=:"+ x );
		
		int y=6;
		System.out.println("y=:"+ ++y);
		System.out.println("y=:"+ y);
		
		int p=6;
		System.out.println("p=:"+ --p);
		System.out.println("p=:"+ p);
		
	}

}
